import React, { useState } from 'react'
import { useReducer } from 'react'
import Form from '../Components/Form'

//Este componente debera ser estilado como "dark" o "light" dependiendo del theme del Context



const Contact = () => {
  const [usuario, dispatch] = useReducer(reducer,initialState)
  const [show, setShow] = useState(false)
  const handleSubmit = (event) => {
    event.preventDefault()
    setShow(true)
  }
  const initialState = {
    name: "",
    email: ""
  }

  const reducer = (usuario, action) => {
    switch (action.type) {
      case 'name':
        return { name: action.payload, ...usuario}
      case 'email':
        return { email: action.payload, ...usuario}
      default:
        throw new Error
    }
  }
  return (
    <div>
      <h2>Want to know more?</h2>
      <p>Send us your questions and we will contact you</p>
      <Form onSubmit= {handleSubmit}>
        <label>Name</label>
        <input type="text" onChange={(event)=> dispatch({type:'name', payload: event.target.value})}/>
        <label>Name</label>
        <input type="text" onChange={(event)=> dispatch({type:'email', payload: event.target.value})}/>
        <button>Enviar</button>
      </Form>
      {show && <h3>Gracias {usuario.name} te contactaremos cuanto antes v√≠a email</h3>}
    </div>
  )
}

export default Contact
